                <h1 className='mt-5'>Invest in everything</h1>
                <p>Online platform to invest in stocks, derivatives, mutual funds and more</p>
                <button className='p-3 btn btn-primary' style={{width:"30%", margin: "0 auto"}}>Signup now</button>

            </div>
        
        </div>
    );
}

export default Hero;
