import React from 'react';

function Education() {
    return ( 
        <div className='container ' >
            <div className='row'>
               <div className='col-6'>
                    <img src='media/images/education.svg'></img>
               </div>
                <div className='col-6'>
                    <h2>Free and open market education</h2>
                    <p>Varsity, the largest online stock market eduation book in the world covering everything from the basics to advance trading.</p>
                    <a href='' style={{textDecoration:"none"}}>Versity <i class="fa fa-long-arrow-right" aria-hidden="true" ></i></a>
                    <p>TradingQ&A, the most active trading and investment community in India for all your market related queries.</p>
                    <a href='' style={{textDecoration:"none"}}>TradingQ&A <i class="fa fa-long-arrow-right" aria-hidden="true" ></i></a>
                </div>
            </div>

        </div>
     
     );
}

export default Education;
